import { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import client from "../client";
import SanityBlockContent from "@sanity/block-content-to-react";

export default function SinglePost() {
  const [singlePost, setSinglePost] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const { slug } = useParams();

  useEffect(() => {
    client
      .fetch(
        `*[slug.current == "${slug}"] {
        title,
        body,
        mainImage {
          asset-> {
            _id,
            url
          },
          alt
        }

      }`
      )
      .then((data) => setSinglePost(data[0]));
    setIsLoading(false);
  }, [slug]);
  return (
    <>
      {isLoading ? (
        <h1>Loading...</h1>
      ) : (
        <section className="px-5 xl:max-w-6xl xl:mx-auto pb-20">
          <h1 className="uppercase font-bold text-4xl mt-5 mb-10 tracking-wide mb-10  md:text-6xl lg:text-8xl text-center mt-5">
            {singlePost.title}
          </h1>
          {singlePost.mainImage && singlePost.mainImage.asset && (
            <img
              src={singlePost.mainImage.asset.url}
              alt={singlePost.title}
              title={singlePost.title}
              className="blog__image rounded-t"
            />
          )}
          <p>By Admin</p>
          <div className="block__content">
            <SanityBlockContent
              blocks={singlePost.body}
              projectid="s71scqva"
              dataset="production"
            />
          </div>

          <button className="py-2 px-6 rounded shadow text-white bg-black hover:bg-transparent border-2 border-black transition-all duration-500 hover:text-black font-bold">
            <Link to="/blog">Read More Articles</Link>
          </button>
        </section>
      )}
    </>
  );
}
